import React,{useState,useEffect} from "react";
import {getBookDetail} from "../../controllers/book";
import { getUser,isUserAuth } from "../../controllers/user";
import CartItem from "./cartitem";
const Cart=(props)=>{
    const [books,setBooks] = useState([]);
    useEffect(()=>{
        let {borrowed,find_paid,fee} = props.user;
        if(borrowed)borrowed.map((e)=>{
          getBookDetail(e.book_id)
          .then((resp)=>{
            let flag=1;
            resp.takendate=e.takendate;
            for(const item of books){
              if(item._id==resp._id){flag=0;break;}
            }
            if(flag){
            setBooks([...books,resp]);
            }
          })
          .catch((err)=>{
               console.log(err);
          })
        });
        },[props.user]);
    


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    <h2>Books Taken</h2><br/>
    { books?books.map((e)=><CartItem key={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies} takendate={e.takendate}></CartItem>):<></>} 
   </div>);

}
export default Cart;
