import React,{useState} from "react";
import { signIn } from "../controllers/user";
import { useNavigate } from "react-router-dom";
import "./signin.css";
 const SignIn = () =>{
         const navigate = useNavigate();
         const [err, seterr] = useState("");
        const Submit = (data) =>{
              data = data.target;
              let x = {
                gmail:data[0].value,
                password:data[1].value
              };
              signIn(x).then((resp)=>{
              //  console.log(resp);
                if(resp.data.err!=1){
                  localStorage.setItem("libmanager",JSON.stringify(resp.data));
                 navigate("/");
                  }
                  else{
                  //  console.log("HELLO");
                      seterr("Invalid Credentials");
                  }
              })
              .catch((err)=>{
                console.log(err);
              })
        }
    return (
        <div class="main">
          <p class="sign" align="center">Sign in</p>
          {err.length>0?<p className="" align="center" style={{color:"red",fontSize:"15px",fontFamily:"bold"}} >{err}</p>:<></>}
          
        
          <form class="form1" style={{paddingTop:"10px"}} onSubmit={(e)=>{e.preventDefault();Submit(e);}}>
            <input class="un " type="text" align="center" placeholder="Gmail"/>
            <input class="pass" type="password" align="center" placeholder="Password"/>
            <button class="submit" align="center" type="submit">Sign in</button>
            <p class="forgot" align="center"><a href="/forgot">Forgot Password?</a></p>
               </form>   
                      
          </div>);


}
export default SignIn;
