import React,{useEffect,useState} from "react";
import Navbar from "./components/navbar";
import Home from "./components/home";
import {Route,Routes} from "react-router-dom";
import { getBooks } from "./controllers/book";
import SignIn from "./components/signin";
import SignUp from "./components/signup";
import AddBook from "./components/addBook";
import Cart from "./components/cart";
import { isUserAuth,getUser } from "./controllers/user";
function App() {
  const [books,setBooks] = useState([]);
  const [user,setUser] = useState({});
 useEffect(()=>{
 getBooks().then((resp)=>{

    let flag=1;
    if(resp && books.length===resp.length){let f=0;
      for(let i=0;i<books.length;i++){
      if(books[i]!==resp[i]){flag=1;}}
    if(f===0){flag=0;}}
  if(flag){setBooks(resp);}
});
  getUser().then((resp)=>{
   // console.log(resp.data.resp);
    setUser(resp.data.resp);
  })

 },[books]);




  return (
    <div className="App">
           <Navbar/>
           <Routes>
            <Route path='/signin' element={<SignIn/>}/>
            <Route path='/signup' element={<SignUp/>}/>
            <Route path='/addbook' element={<AddBook/>}/>
            {isUserAuth() && isUserAuth().token && isUserAuth().data?<Route path="/cart" element={<Cart/>}/>:<></>}
            <Route path='/' element={<Home books={books} />}/>


           </Routes>

    </div>
  );
}

export default App;
