import React,{useState,useEffect} from "react";
import {getBookDetail} from "../../controllers/book";
import { getUser,isUserAuth } from "../../controllers/user";
import CartItem from "./cartitem";
const getCartBooks = (y) =>{
  let x = [{}];
  let user = y.user;
  if(user.borrowed){

    user.borrowed.map((e)=>{
      getBookDetail(e.book_id).then((resp)=>{
             let sz = y.books.length;
             let flag=1;
             for(let i=0;i<sz;i++){
              if(y.books[i]==resp){flag=0;break;}
             }
             if(flag==1){y.setBooks((prev)=>[...prev,resp]);}
      })
      .catch((err)=>{
        console.log(err);
      })
    });
  }
  
 }
const Cart=(props)=>{
    const [books,setBooks] = useState([]);
    const [cnt,setcnt]= useState(0);
    useEffect(()=>{
          let {borrowed,gmail,reg_no,list,fine_paid,fee,_id} = props.user;
          let x = {borrowed,gmail,reg_no,list,fine_paid,fee,_id};
          let y = {user:x,books,setBooks};
          if(props.user){
          getCartBooks(y);}
        },[props]);
    


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    <p>{cnt}</p>
    {books?books.forEach((e)=><CartItem key={e._id} refno={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies}></CartItem>
    ):<></>}
   </div>);

}
export default Cart;
