import axios from "axios"
import { withRouter } from "react-router-dom";

export const getCart = (props) =>{
    return axios.get(`http://localhost:8000/users/book/borrowed/${props.regno}`)
    .then((resp)=>{
       console.log(resp);
    })
    .catch((err)=>{
        console.log(err);
    })
}
export const signIn = withRouter((props,{history}) =>{
    return axios.post(`http://localhost:8000/signin/user/`,{
        gmail:props.gmail,
        password:props.password
    })
    .then((resp)=>{
        localStorage.setItem("libmanager",JSON.stringify(resp.data));
       history.push("/");
    })
    .catch((err)=>{
        console.log(err);
    })

});
