import axios from "axios"

export const getCart = (props) =>{
    return axios.get(`http://localhost:8000/users/book/borrowed/${props.regno}`)
    .then((resp)=>{
       console.log(resp);
    })
    .catch((err)=>{
        console.log(err);
    })
}
export const signin = (props) =>{
    

    console.log(props.x);
    return  axios.post(`https://localhost:8000/signin/user/`,{
        gmail:props.x.gmail,
        password:props.x.password
    })
    .then((resp)=>{
        localStorage.setItem("libmanager",JSON.stringify(resp.data));
      props.navigate("/");
 
    })
    .catch((err)=>{
        console.log(err);
    })

}
