import React,{useEffect,useState} from "react";
import Navbar from "./components/navbar";
import Home from "./components/home";
import {Route,Routes} from "react-router-dom";
import { getBooks } from "./controllers/book";
import SignIn from "./components/signin";
import SignUp from "./components/signup";
import AddBook from "./components/book/addBook";
import Cart from "./components/cart/cart";
import Cartk from "./components/cart/cartk";
import Profile from "./components/profile";
import { isUserAuth,getUser } from "./controllers/user";
import MangageBook from "./components/admin/givetakebook";
import {getBookDetail} from "./controllers/book";
function App() {
  const [books,setBooks] = useState([]);
  const [user,setUser] = useState({});
  const [loading,setLoading] = useState(0);
  const [borrowed,setBorrowed] = useState([]);
  const [list,setList] = useState([]);
 useEffect(()=>{
 getBooks().then((resp)=>{
    let flag=1;
    if(resp && books.length===resp.length){let f=0;
      for(let i=0;i<books.length;i++){
      if(books[i]!==resp[i]){flag=1;}}
    if(f===0){flag=0;}}
  if(flag){setBooks(resp);}

});
if(isUserAuth()){
  getUser().then((resp)=>{
    setUser(resp.data.resp);
    let {borrowed,list} = resp.data.resp;
    borrowed.map((e)=>{
      getBookDetail(e.book_id)
      .then((resp)=>{

        let flag=1;
        resp.takendate=e.takendate;
        for(const item of books){
          if(item._id==resp._id){flag=0;break;}
        }
        if(flag){

        setBorrowed((b)=>[...b,resp]);

        }
      })
      .catch((err)=>{
           console.log(err);
      })
    });
    list.map((e)=>{
      getBookDetail(e.book_id)
      .then((resp)=>{
        let flag=1;
        resp.takendate = e.takendate;
        resp.givendate = e.givendate;
        for(const item of list){
          if(item._id == resp._id && item.takendate==resp.takendate && item.givendate == resp.givendate){
            flag=0;break;
          }
        }
        if(flag==1 ){setList((prev)=>[...prev,resp]);}
      })
      .catch((err)=>{
        console.log(err);
      })
     });


  }
    )
    .catch((err)=>{
      console.log(err);
    })
}

 },[books]);

  return (
    <div className="App">
           <Navbar/>
           <Routes>
            <Route path='/signin' element={<SignIn/>}/>
            <Route path='/signup' element={<SignUp/>}/>
            <Route path='/addbook' element={<AddBook/>}/>
            <Route path="/cart" element={<Cartk list={list} books={borrowed}/>}/>
            <Route path="/profile" element={<Profile/>}/>
            <Route path="/managebook" element={<MangageBook/>}/>
            <Route exact path='/' element={<Home books={books} load={loading}/>}/>


           </Routes>

    </div>
  );
}

export default App;
