import React from 'react'
import LoadingScreen from 'react-loading-screen'

const Loading = (props) =>{

 
    return (
      <div>
        <LoadingScreen loading={props.load}
        loader="bubble-spinner"
    bgColor='#f1f1f1'
    spinnerColor='#9ee5f8'
    textColor='#676767'/>
       
      </div>
    )
    }

export default Loading;
