import React from "react";
import { addProduct } from "../controllers/product";
const AddProduct = () =>{
   const submitForm = (data) =>{
    data = data.target;
    let product = {
        name:data[0].value,
        price:data[1].value
    }
    addProduct(product);
   }
    return (
        <div className="main">
          <p className="sign" align="center">New Product</p>
          <form className="form1" onSubmit={(e)=>{e.preventDefault();submitForm(e)}}>
            <input className="un " type="text" align="center" placeholder="Product Name"/>
            <input className="un " type="text" align="center" placeholder="Price"/>
            <button className="submit" align="center" type="submit">Add Product</button>
          </form>           
          </div>);

}
export default AddProduct;
