import React,{useState} from "react";
import getBookDetail from "../controllers/book";
import CartItem from "./cartitem";
const Cart=(props)=>{
    //const [cart,setCart] =  useState([{}]);
    console.log(props.user);
   
   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
   {props.user.borrowed?props.user.borrowed.map((e)=>{
        return getBookDetail(e.id).then((resp)=>{
           return  <CartItem key={resp.refno} name={resp.name} author={resp.author} publishedDate={resp.publishedDate} no_of_copies={resp.no_of_copies} />
        })
        .catch((err)=>{
            return <></>;
        })
         
    }):<></>}
   </div>)

}
export default Cart;
