import React,{useState,useEffect} from "react";
import {getBookDetail} from "../../controllers/book";
import { getUser,isUserAuth } from "../../controllers/user";
import CartItem from "./cartitem";
const Cart=(props)=>{
    const [books,setBooks] = useState(new Set([]));
    const [cnt,setcnt]= useState(0);
    

    useEffect(()=>{
        let {borrowed,find_paid,fee} = props.user;
        borrowed?borrowed.map((e)=>{
          getBookDetail(e.book_id)
          .then((resp)=>{
             setBooks((prev)=>new Set([...prev,resp]));
          })
          .catch((err)=>{
               console.log(err);
          })
        }):"";
        },[props.user]);
    


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    {books?books.forEach((e)=><CartItem key={e._id} refno={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies}></CartItem>
    ):<></>}
   </div>);

}
export default Cart;
