import React,{useState,useEffect} from "react";
import {getBookDetail} from "../controllers/book";
import { getCart, getUser,isUserAuth } from "../controllers/user";
import CartItem from "./cartitem";
const Cart=()=>{
    //const [cart,setCart] =  useState([{}]);
    const [user,setUser] = useState({});
    const [books,setBooks] = useState([]);
    const getCartBooks = () =>{
      if(user.borrowed){
        user.borrowed.map((e)=>{
          getBookDetail(e.book_id).then((resp)=>{
            setBooks([...books,resp]);
          })
          .catch((err)=>{
            console.log(err);
          })
        })
      }
     }
    useEffect(()=>{
     
       if(isUserAuth()){
         getUser().then((resp)=>{
           setUser(resp.data.resp);
           getCartBooks();
           //console.log(user);
         })
       }
        },[]);
    
 // getBookDetail(user.borrowed[0].book_id);


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    {books?books.map((e)=>{
      return <CartItem refno={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies}></CartItem>
    }):<></>}
         
   
   </div>)

}
export default Cart;
