import axios from "axios"


export const getCart = (props) =>{
    return axios.get(`http://localhost:8000/users/book/borrowed/${props.regno}`)
    .then((resp)=>{
       console.log(resp);
    })
    .catch((err)=>{
        console.log(err);
    })
}
export const signIn = (props) =>{
    
    
    return  axios.post(`http://localhost:8000/signin/user/`,{
        gmail:props.x.gmail,
        password:props.x.password
    })
    .then((resp)=>{
        if(resp.status===200){
        localStorage.setItem("libmanager",JSON.stringify(resp.data));
       props.navigate("/");
        }
        else{
            props.seterr("Invalid Credentials");
        }
 
    })
    .catch((err)=>{
        console.log(err);
    })

}
