import React,{useState} from "react";

import "../signin.css";
 const RemoveBook = () =>{
     const [err,seterr] = useState("");
     const Submit = (e) =>{
       let refno = e.target.data[0];
       console.log(refno);
     }
    return (
        <div className="main">
          <p className="sign" align="center">Remove Book</p>
          
          
          {err.length>0?<p className="" align="center" style={{color:"red",fontSize:"17px",fontFamily:"Ubuntu"}} >{err}</p>:<></>}
            
          <form className="form1" style={{paddingTop:"10px"}} onSubmit={(e)=>{e.preventDefault();Submit(e)}}>
            <input className="un " type="text" align="center" placeholder="Ref no" />
            <button className="submit" align="center" type="submit">Remove</button>
          
               </form>   
                      
          </div>);


}
export default RemoveBook;
