import React from "react";


const CartItem = (prod)=>{
    console.log(prod);
    
    return (<div className="" style={{height:"250px",width:"225px",
    backgroundColor:"white",color:"black",borderColor:"black",boxShadow:"1px 1px 2px 2px black",borderRadius:"10px"
    ,textAlign:"center",marginRight:"4rem",marginTop:"3rem"}}>
    <img className="mt-1" src="https://imgs.search.brave.com/uyxX887TwVCa_cqS1RuuTSTD_21h1JD3snUun-Z2Ycs/rs:fit:844:225:1/g:ce/aHR0cHM6Ly90c2Uy/Lm1tLmJpbmcubmV0/L3RoP2lkPU9JUC5p/NjNjR1k0RERmQnZm/b3NpTFlVMVp3SGFF/SyZwaWQ9QXBp" height="100px" width="auto" alt="no-image"></img>
    <h4 className="">{prod.name}</h4>
    <div className="" >
    <h6 className="" style={{}}>author : {prod.author}</h6>
    <h6  className="" style={{}}>status : {prod.no_of_copies>0?"Available":"Not available"} </h6>
    </div>
    </div>);
}
export default CartItem;
