import axios from "axios"

export const getCart = (props) =>{
    return axios.get(`http://localhost:8000/users/book/borrowed/${props.regno}`)
    .then((resp)=>{
       console.log(resp);
    })
    .catch((err)=>{
        console.log(err);
    })
}
export const signIn = (props) =>{
    return axios.post(`http://localhost:8000/signin/user/`,{
        gmail:props.gmail,
        password:props.password
    })
    .then((resp)=>{
        console.log(resp);
        return resp;
    })
    .catch((err)=>{
        console.log(err);
    })

}
