import React,{useState,useEffect} from "react";
import {getBookDetail} from "../../controllers/book";
// import { getUser,isUserAuth } from "../../controllers/user";
import CartItem from "./cartitem";
const Cart=(props)=>{
    const [books,setBooks] = useState([]);
    const [his,setHis] = useState([]);
    useEffect(()=>{
        let {borrowed,list} = props.user;
        if(borrowed)borrowed.map((e)=>{
          getBookDetail(e.book_id)
          .then((resp)=>{
            let flag=1;
            resp.takendate=e.takendate;
            for(const item of books){
              if(item._id==resp._id){flag=0;break;}
            }
            if(flag){
            setBooks((b)=>[...b,resp]);
            }
          })
          .catch((err)=>{
               console.log(err);
          })
        }
        
        );
        if(list){
           list.map((e)=>{
            getBookDetail(e.book_id)
            .then((resp)=>{
              let flag=1;
              resp.takendate = e.takendate;
              resp.givendate = e.givendate;
              for(const item of his){
                if(item._id == resp._id && item.takendate==resp.takendate && item.givendate == resp.givendate){
                  flag=0;break;
                }
              }
              if(flag==1){setHis((prev)=>[...prev,resp]);}
            })
            .catch((err)=>{
              console.log(err);
            })
           })
          
        }
        },[props.user]);
    


   return (
   <div>
    <p className="p-3 mt-2 ms-5 " style={{fontFamily:"Ubuntu",fontSize:"30px",textAlign:"center"}} ><u>Books Taken</u></p>
   <div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    { books?books.map((e)=><CartItem key={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies} takendate={e.takendate}></CartItem>):<></>} 
   </div>
   <div>
   <p className="p-3 mt-2 ms-5 " style={{fontFamily:"Ubuntu",fontSize:"30px",textAlign:"center"}} ><u>Books History</u></p>
    <div className="d-flex flex-wrap mt-2 ms-5 mr-5">
    {his?his.map((e)=><CartItem key={e.refno} name={e.name} author={e.author} publishedDate={e.publishedDate} no_of_copies={e.no_of_copies} takendate={e.takendate} givendate = {e.givendate}/>):<></>}
    </div>
   </div>
   <p className="mt-5 ms-5" style={{fontFamily:"Ubuntu",fontSize:"20px"}}><u>Fine To be Paid</u> : {props.user.fee}</p>
   <p className="mt-5 ms-5" style={{fontFamily:"Ubuntu",fontSize:"20px"}}><u>Fine Paid Till Now</u> : {props.user.find_paid}</p>
   </div>
   );
}
export default Cart;
