import React,{useState} from "react";
import { useNavigate } from "react-router-dom";
import {BookManage} from "../../controllers/book";
import "../signin.css";
 const MangageBook = () =>{
    const [err,seterr] = useState("");
    const [take,settake]  = useState(1);
    const optionSelection = () =>{
        let val = document.getElementById("ctx").value;
        seterr("");
        if(val==="student giving"){settake(0);}
        else{
          settake(1);
        }
    }     
    const navigate = useNavigate();
         
         const Submit = (book) =>{
            let b = book.target;
            let x = {
                reg_no:b[0].value,
                ref_no:b[1].value
            };
            //console.log(x);
            mangageBook(x);
         }
    return (
        <div className="main">
          <p className="sign" align="center">Book Management</p>
          <form className="form1" style={{paddingTop:"10px"}} onSubmit={(e)=>{e.preventDefault();Submit(e);}}>
            <input className="un " type="text" align="center" placeholder="Admission No" onChange={()=>{seterr("");}}/>
            <input className="un " type="text" align="center" placeholder="Book Reference" onChange={()=>{seterr("");}}/>
            <select className="un" type="text" id="ctx" onChange={(e)=>optionSelection()}>
              <option className="un" value="student">Student taking</option>
              <option className="un" value="admin">Student giving</option>
            </select>
            {err.length>0?<p className="" align="center" style={{color:"red",fontSize:"13px"}} >{err}</p>:<></>}
            <button className="submit" align="center" type="submit">Submit</button>
            
             </form>   
                      
          </div>);


}
export default MangageBook;
