import React,{useState,useEffect} from "react";
import {getBookDetail} from "../controllers/book";
import { getCart, getUser,isUserAuth } from "../controllers/user";
import CartItem from "./cartitem";
const Cart=()=>{
 // console.log(props);
    //const [cart,setCart] =  useState([{}]);
    const [user,setUser] = useState({});
    const [books,setBooks] = useState([]);
    const getCartBooks = () =>{
    
    
      console.log(user);
      if(user.borrowed){
        
        let x = [{}];
      
        user.borrowed.map((e)=>{
          getBookDetail(e.book_id).then((resp)=>{
            x.push(resp);
          })
          .catch((err)=>{
            console.log(err);
          })
        });
       // x.splice(1,1);
        setBooks(x);
      }
     }
    useEffect(()=>{
     
       if(isUserAuth()){
        getUser().then((resp)=>{
          setUser(resp.data.resp);
          return getCartBooks();
        })
        .catch((err)=>{console.log(err);})
         
          
         
       }
        },[]);
    
 // getBookDetail(user.borrowed[0].book_id);


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    {books?books.map((e)=>{
     e._id?
       <CartItem key={e._id} refno={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies}></CartItem>
    :<></>}):<></>}
         
   
   </div>)

}
export default Cart;
