import CartItem from "./cartitem";
const getCartBooks = (user,setBooks) =>{
  if(user.borrowed){
    
    let x = [{}];
  
    user.borrowed.map((e)=>{
      getBookDetail(e.book_id).then((resp)=>{
        x.push(resp);
      })
      .catch((err)=>{
        console.log(err);
      })
    });
    x.splice(0,1);
    setBooks(x);
  }
 }
const Cart=(props)=>{
