        //   if(resp.data.resp!=user){
        //     setUser(x);getCartBooks(x,setBooks);
        //   }
        //   // setUser(x);
        //   // if(!user.gmail || user.borrowed!==x.borrowed || user.gmail!==x.gmail || x.reg_no!==user.gmail || x.list!=user.list){
        //   // getCartBooks(x,setBooks);
        //   // }
        // })
        // .catch((err)=>{console.log(err);})
        getCartBooks(props.books,setBooks);
       }
        },[]);
    


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    {books?books.map((e)=><CartItem key={e._id} refno={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies}></CartItem>
    ):<></>}
         
   
   </div>)

}
export default Cart;
