import React,{useState,useEffect} from "react";
import {getBookDetail} from ".../controllers/book";
import { getUser,isUserAuth } from ".../controllers/user";
import CartItem from "./cartitem";
const Cart=()=>{
    const [user,setUser] = useState({});
    const [books,setBooks] = useState([]);
    const getCartBooks = (user) =>{
      if(user.borrowed){
        
        let x = [{}];
      
        user.borrowed.map((e)=>{
          getBookDetail(e.book_id).then((resp)=>{
            x.push(resp);
          })
          .catch((err)=>{
            console.log(err);
          })
        });
        x.splice(0,1);
        setBooks(x);
      }
     }
    useEffect(()=>{
     
       if(isUserAuth()){
        getUser().then((resp)=>{ 
          let {borrowed,gmail,reg_no,list,fine_paid,fee,_id} = resp.data.resp;
          let x = {borrowed,gmail,reg_no,list,fine_paid,fee,_id};
          if(!user.gmail || user.borrowed!==x.borrowed || user.gmail!==x.gmail || x.reg_no!==user.gmail || x.list!=user.list){
          setUser(x);
         // console.log(x);
          getCartBooks(x);
          }
        })
        .catch((err)=>{console.log(err);})
         
          
         
       }
        },[]);
    


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    {books?books.map((e)=><CartItem key={e._id} refno={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies}></CartItem>
    ):<></>}
         
   
   </div>)

}
export default Cart;
