import React,{useState,useEffect} from "react";
import {getBookDetail} from "../../controllers/book";
import { getUser,isUserAuth } from "../../controllers/user";
import CartItem from "./cartitem";
const Cart=(props)=>{
    const [books,setBooks] = useState([]);
    const [cnt,setcnt]= useState(0);
    

    useEffect(()=>{
          let {borrowed,gmail,reg_no,list,fine_paid,fee,_id} = props.user;
          let x = {borrowed,gmail,reg_no,list,fine_paid,fee,_id};
          const getCartBooks = () =>{
            let x = [{}];
            let user = props.user;
            if(user.borrowed){
          
              user.borrowed.map((e)=>{
                getBookDetail(e.book_id).then((resp)=>{
                       let sz = books.length;
                       let flag=1;
                       for(let i=0;i<sz;i++){
                        if(books[i]._id==resp._id){
                          console.log(books[i]._id, resp._id);
                          flag=0;break;}
                       }
                       console.log(flag);
                       console.log(resp,books);
                       if(flag==1){setBooks((prev)=>[...prev,resp]);}
                })
                .catch((err)=>{
                  console.log(err);
                })
              });
            }
            
           }
          if(props.user){
          getCartBooks();}
        },[]);
    


   return (<div className="d-flex flex-wrap mt-2 ms-5 mr-5" style={{}} >
    {books?books.map((e)=><CartItem key={e._id} refno={e.refno} name={e.name} author={e.author} publishedDate = {e.publishedDate} no_of_copies={e.no_of_copies}></CartItem>
    ):<></>}
   </div>);

}
export default Cart;
