import axios from "axios";
export const getBooks = ()=>{
        return  axios.get("http://localhost:8000/users/book/").then((resp)=>{
            return resp.data.resp;

         })
         .catch((err)=>{
            console.log(err);
            return err;
         })
};
export const addBook = (book) =>{
   return axios.post("http://localhost:8000/admin/book/",{
        book
   }).then((resp)=>{
      console.log(resp);
      return resp;
   })
   .catch((err)=>{
      console.log(err);
   })

};
export const getBookDetail = (props) =>{
   console.log(props);
   return axios.get(`http://localhost/8000/users/book/${props}`)
   .then((resp)=>{
      console.log(resp);
      return resp;
      
   })
   .catch((err)=>{
      console.log(err);
   })
}
